//A component is a JS function
import React,{useState} from 'react'
import './ExpenseItem.css'
import ExpenseDate from './ExpenseDate';
import Card from '../UI/Card';
import NewExpense from '../NewExpense'
function ExpenseItem(props) {
    const [title,setTitle]=useState(props.title)
    
    const buttonHandler=()=>
    {
        setTitle("new title");
        console.log(title)
    }
    return (
        <Card className="expense-item">
            <ExpenseDate month={props.date.toLocaleDateString('en-US',{month:'long'})} year={props.date.toLocaleDateString('en-US',{year:'numeric'})} day={props.date.toLocaleDateString('en-US',{day:'numeric'})}></ExpenseDate>
            <div className="expense-item__description">
                <h2>{title}</h2>
                <div className="expense-item__price">
                    ${props.amount}
                </div>
            </div>
            <button onClick={buttonHandler}>Change Item</button>
            
        </Card>
    );
}
export default ExpenseItem;
