import React,{useState} from 'react';
import './ExpenseForm.css'
const ExpenseForm=()=>
{
    const [newTitle,titleChanged]=useState('')
    const [newAmount,amountChanged]=useState('')
    const [newDate,DateChanged]=useState('')

    const newTitleUpdated=(event)
    return(
        <form>
            <div className="new-expense__controls">
                <div className="new-expense__control">
                    <label>Title</label>
                    <input type="text" onChange={newTitleUpdated}/>
                </div>
                <div className="new-expense__control">
                    <label>Amount</label>
                    <input type="number" onChange={newAmountUpdated}/>
                </div>
                <div className="new-expense__control">
                    <label>Date</label>
                    <input type="date" onChange={newDateUpdated}/>
                </div>
                <button type="submit">SUBMIT</button>
            </div>
            
        </form>
    )
}

export default ExpenseForm;
