import ExpenseItem from './ExpenseItem';
import React, { useState } from 'react';
import ExpensesFilter from './ExpensesFilter';
import Card from '../UI/Card';
import './Expenses.css';


function Expenses(props) {
  const [selectedYear, yearSelection] = useState('2022')
  const yearChangingFun = (selectedYear) => {
    yearSelection(selectedYear)
  }

  let expensesContent = <p>NO EXPENSES FOR THIS YEAR</p>

  const filteredExpenses = props.items.filter(expense => {
    return expense.date.getFullYear().toString() === selectedYear;
  });

  if (expensesContent.length > 0) {
    expensesContent = filteredExpenses.map(expense => <ExpenseItem title={expense.title}
      amount={expense.amount}
      date={expense.date}></ExpenseItem>)

  }

  return (
    <Card className="expenses">
      <ExpensesFilter selected={selectedYear} yearOnChange={yearChangingFun}></ExpensesFilter>
      {expensesContent}


    </Card>
  );
}

export default Expenses;
