import React,{useState} from 'react';
import './ExpenseForm.css'
const ExpenseForm=(props)=>
{
    const [newTitle,titleChanged]=useState('')
    const [newAmount,amountChanged]=useState('')
    const [newDate,dateChanged]=useState('')

    const newTitleUpdated=(event)=>
    {
        titleChanged(event.target.value)
    }

    const newAmountUpdated=(event)=>
    {
        amountChanged(event.target.value)
    }

    const newDateUpdated=(event)=>
    {
        dateChanged(event.target.value)
    }

    const formSubmitted=(event)=>{
        event.preventDefault()
        const newExpense={
            title:newTitle,
            amount:newAmount,
            date:new Date(newDate)
        }
        titleChanged('')
        amountChanged('')
        dateChanged('')
        //console.log(newExpense)

        props.onReceiv(newExpense)
    }

    

    return(
        <form onSubmit={formSubmitted}>
            <div className="new-expense__controls">
                <div className="new-expense__control">
                    <label>Title</label>
                    <input type="text" value={newTitle} onChange={newTitleUpdated}/>
                </div>
                <div className="new-expense__control">
                    <label>Amount</label>
                    <input type="number" value={newAmount} onChange={newAmountUpdated}/>
                </div>
                <div className="new-expense__control">
                    <label>Date</label>
                    <input type="date" value={newDate} onChange={newDateUpdated}/>
                </div>
                <button type="submit">SUBMIT</button>
            </div>
            
        </form>
    )
}

export default ExpenseForm;
